// â•â•â•â•â•â•â•â•â•â• Weekly study analysis â•â•â•â•â•â•â•â•â•â•

export function generateWeeklyAnalysis(context: any, name: string): string {
  const parts: string[] = [];
  parts.push(`ğŸ“Š **AnÃ¡lise Semanal de ${name}**\n`);
  
  // XP and level
  parts.push(`â­ **NÃ­vel ${context.level}** (${context.levelTitle}) â€” ${context.totalXP} XP`);
  parts.push(`ğŸ”¥ Ofensiva: **${context.studyStreak} dia(s)**\n`);
  
  // Performance
  if (context.simuladosDone > 0) {
    parts.push(`ğŸ“ **Simulados:** ${context.simuladosDone} feitos (mÃ©dia: ${context.simuladoAvgScore || 0}%)`);
    if ((context.simuladoAvgScore || 0) >= 70) parts.push(`   â†’ Ã“timo desempenho! Continue assim! ğŸŒŸ`);
    else if ((context.simuladoAvgScore || 0) >= 50) parts.push(`   â†’ Bom progresso! Foque nas Ã¡reas fracas pra subir!`);
    else parts.push(`   â†’ Precisa intensificar os estudos. Vamos montar um plano!`);
  }
  
  // Weak/strong areas
  if (context.weakAreas?.length > 0) {
    parts.push(`\nâš ï¸ **Ãreas que precisam de atenÃ§Ã£o:** ${context.weakAreas.join(', ')}`);
    parts.push(`   â†’ Recomendo: 40% do tempo de estudo nessas Ã¡reas`);
  }
  if (context.strongAreas?.length > 0) {
    parts.push(`ğŸ’ª **Ãreas fortes:** ${context.strongAreas.join(', ')}`);
    parts.push(`   â†’ Mantenha com revisÃ£o leve (20% do tempo)`);
  }
  
  // Recommendations
  parts.push(`\nğŸ“‹ **RecomendaÃ§Ãµes da semana:**`);
  if (context.pomodorosDone < 10) parts.push(`   - ğŸ… Aumente os Pomodoros! Meta: pelo menos 3 por dia`);
  if ((context.flashcardsMastered || 0) < (context.flashcardsTotal || 1) * 0.5) parts.push(`   - ğŸƒ Revise mais flashcards â€” ${context.flashcardsTotal - (context.flashcardsMastered || 0)} pendentes`);
  if (context.tasksOverdue > 0) parts.push(`   - â° Resolva as ${context.tasksOverdue} tarefas atrasadas!`);
  parts.push(`   - ğŸ“– FaÃ§a pelo menos 1 simulado completo esta semana`);
  parts.push(`   - âœï¸ Escreva pelo menos 1 redaÃ§Ã£o`);
  
  parts.push(`\nQuer que eu monte um plano detalhado? ğŸ˜Š`);
  return parts.join('\n');
}

